'''	This file is responsible for parsing the command line arguments.'''import argparseclass CMDLineParser(object):		def __init__(self, description):		'''			Initializes the arg parser that is required for command line parsing		'''		self.parser = argparse.ArgumentParser(description=description)		def parseOptions(self):		'''			Parses the options		'''		return self.parser.parse_args()			def make_options(self, *args, **kwargs):		'''			Generic function to add arguments to the command line parser		'''		self.parser.add_argument(*args, **kwargs)		